<?php/** * Home模块基类 * * @author Aoki */ class BaseAction extends Action {		/*	 * 项目初始化	 */	function _initialize() {			}		/*	 * 404 错误定向	 */	protected function _404($message = '', $jumpUrl = '', $waitSecond = 3) {		$this->assign('message', L('pagenotfound'));		if (!empty($jumpUrl)) {			$this->assign('jumpUrl', $jumpUrl);			$this->assign('waitSecond', $waitSecond);		}		$this->display(C('TMPL_ACTION_ERROR'));	}}